import { Component } from '@angular/core';

@Component({
  selector: 'app-notes',
  templateUrl: './notes.component.html',
  styleUrls: ['./notes.component.scss']
})
export class NotesComponent {

}
<div class="notes-wrapper">
  <div class="container">
    <div class="notes-list">
      <div class="note-card">
        <div class="note-card-title">this is a title.</div>
        <div class="note-card-content">this is a note content</div>
      </div>
    </div>
  </div>
</div>
