import { Component } from '@angular/core';
import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-tabs',
  templateUrl: './tabs.component.html',
  styleUrls: ['./tabs.component.scss'],
})
export class TabsComponent {
  constructor(private router: Router) {}

  changeBookmarks() {
    alert(122);
    console.log(3452345);
    this.router.navigate(['/bookmarks']);
  }
}
<div class="tabs">
  <a
    routerLink="bookmarks"
    routerLinkActive="selected"
    class="tab bookmarks-tab"
  >
    <div class="tab-tile">
      <i class="material-symbols-outlined">bookmarks</i>
    </div>
  </a>
  <a routerLink="todos" routerLinkActive="selected" class="tab checks-tab">
    <div class="tab-tile checks-tab">
      <i class="material-symbols-outlined">check</i>
    </div>
  </a>
  <a routerLink="notes" routerLinkActive="selected" class="tab notes-tab">
    <div class="tab-tile notes-tab">
      <i class="material-symbols-outlined">notes</i>
    </div>
  </a>
</div>
